(function() {
  this.disableSaveOnClick = function() {
    return ($('form.edit_order')).submit(function() {
      return ($(this)).find(':submit, :image').attr('disabled', true).removeClass('primary').addClass('disabled');
    });
  };

  $(function() {
    var country_from_region, get_states, get_states_required, update_state;
    if (($('#checkout_form_address')).is('*')) {
      ($('#checkout_form_address')).validate();
      country_from_region = function(region) {
        return ($('p#' + region + 'country' + ' span#' + region + 'country-selection :only-child')).val();
      };
      get_states = function(region) {
        return state_mapper[country_from_region(region)];
      };
      get_states_required = function(region) {
        return states_required_mapper[country_from_region(region)];
      };
      update_state = function(region) {
        var selected, state_input, state_para, state_select, state_span_required, states, states_required, states_with_blank;
        states = get_states(region);
        states_required = get_states_required(region);
        state_para = $('p#' + region + 'state');
        state_select = state_para.find('select');
        state_input = state_para.find('input');
        state_span_required = state_para.find('state-required');
        if (states) {
          selected = parseInt(state_select.val());
          state_select.html('');
          states_with_blank = [['', '']].concat(states);
          $.each(states_with_blank, function(pos, id_nm) {
            var opt;
            opt = ($(document.createElement('option'))).attr('value', id_nm[0]).html(id_nm[1]);
            if (selected === id_nm[0]) {
              opt.prop('selected', true);
            }
            return state_select.append(opt);
          });
          state_select.prop('disabled', false).show();
          state_input.hide().prop('disabled', true);
          return state_span_required.show();
        } else {
          state_select.hide().prop('disabled', true);
          state_input.show();
          if (states_required) {
            state_span_required.show();
          } else {
            state_input.val('');
            state_span_required.hide();
          }
          state_para.toggle(!!states_required);
          return state_input.prop('disabled', !states_required);
        }
      };
      ($('p#bcountry select')).change(function() {
        return update_state('b');
      });
      ($('p#scountry select')).change(function() {
        return update_state('s');
      });
      update_state('b');
      update_state('s');
      ($('input#order_use_billing')).click(function() {
        if (($(this)).is(':checked')) {
          ($('#shipping .inner')).hide();
          return ($('#shipping .inner input, #shipping .inner select')).prop('disabled', true);
        } else {
          ($('#shipping .inner')).show();
          ($('#shipping .inner input, #shipping .inner select')).prop('disabled', false);
          if (get_states('s')) {
            return ($('span#sstate input')).hide().prop('disabled', true);
          } else {
            return ($('span#sstate select')).hide().prop('disabled', true);
          }
        }
      }).triggerHandler('click');
    }
    if (($('#checkout_form_payment')).is('*')) {
      ($('input[type="radio"][name="order[payments_attributes][][payment_method_id]"]')).click(function() {
        ($('#payment-methods li')).hide();
        if (this.checked) {
          return ($('#payment_method_' + this.value)).show();
        }
      });
      return ($('input[type="radio"]:checked')).click();
    }
  });

}).call(this);
(function() {
  var add_image_handlers, show_variant_images, update_variant_price;

  add_image_handlers = function() {
    var thumbnails;
    thumbnails = $('#product-images ul.thumbnails');
    ($('#main-image')).data('selectedThumb', ($('#main-image img')).attr('src'));
    thumbnails.find('li').eq(0).addClass('selected');
    thumbnails.find('a').on('click', function(event) {
      ($('#main-image')).data('selectedThumb', ($(event.currentTarget)).attr('href'));
      ($('#main-image')).data('selectedThumbId', ($(event.currentTarget)).parent().attr('id'));
      ($(this)).mouseout(function() {
        thumbnails.find('li').removeClass('selected');
        return ($(event.currentTarget)).parent('li').addClass('selected');
      });
      return false;
    });
    thumbnails.find('li').on('mouseenter', function(event) {
      return ($('#main-image img')).attr('src', ($(event.currentTarget)).find('a').attr('href'));
    });
    return thumbnails.find('li').on('mouseleave', function(event) {
      return ($('#main-image img')).attr('src', ($('#main-image')).data('selectedThumb'));
    });
  };

  show_variant_images = function(variant_id) {
    var currentThumb, newImg, thumb;
    ($('li.vtmb')).hide();
    ($('li.vtmb-' + variant_id)).show();
    currentThumb = $('#' + ($('#main-image')).data('selectedThumbId'));
    if (!currentThumb.hasClass('vtmb-' + variant_id)) {
      thumb = $(($('ul.thumbnails li:visible.vtmb')).eq(0));
      if (!(thumb.length > 0)) {
        thumb = $(($('ul.thumbnails li:visible')).eq(0));
      }
      newImg = thumb.find('a').attr('href');
      ($('ul.thumbnails li')).removeClass('selected');
      thumb.addClass('selected');
      ($('#main-image img')).attr('src', newImg);
      ($('#main-image')).data('selectedThumb', newImg);
      return ($('#main-image')).data('selectedThumbId', thumb.attr('id'));
    }
  };

  update_variant_price = function(variant) {
    var variant_price;
    variant_price = variant.data('price');
    if (variant_price) {
      return ($('.price.selling')).text(variant_price);
    }
  };

  $(function() {
    add_image_handlers();
    if (($('#product-variants input[type="radio"]')).length > 0) {
      show_variant_images(($('#product-variants input[type="radio"]')).eq(0).attr('value'));
    }
    return ($('#product-variants input[type="radio"]')).click(function(event) {
      show_variant_images(this.value);
      return update_variant_price($(this));
    });
  });

}).call(this);
(function() {
  $(function() {
    if (($('form#update-cart')).is('*')) {
      ($('form#update-cart a.delete')).show().one('click', function() {
        ($(this)).parents('.line-item').first().find('input.line_item_quantity').val(0);
        ($(this)).parents('form').first().submit();
        return false;
      });
    }
    return ($('form#update-cart')).submit(function() {
      return ($('form#update-cart #update-button')).attr('disabled', true);
    });
  });

}).call(this);







SpreePaypalExpress = {
  hideSettings: function(paymentMethod) {
    if (SpreePaypalExpress.paymentMethodID && paymentMethod.val() == SpreePaypalExpress.paymentMethodID) {
      $('.payment-method-settings').children().hide();
      $('#payment_amount').prop('disabled', 'disabled');
      $('button[type="submit"]').prop('disabled', 'disabled');
      $('#paypal-warning').show();
    } else if (SpreePaypalExpress.paymentMethodID) {
      $('.payment-method-settings').children().show();
      $('button[type=submit]').prop('disabled', '');
      $('#payment_amount').prop('disabled', '')
      $('#paypal-warning').hide();
    }
  }
}

$(document).ready(function() {
  checkedPaymentMethod = $('#payment-methods input[type="radio"]:checked');
  SpreePaypalExpress.hideSettings(checkedPaymentMethod);
  paymentMethods = $('#payment-methods input[type="radio"]').click(function (e) {
    SpreePaypalExpress.hideSettings($(e.target));
  });
})
